htmlform.script:

if (jQuery) {

    jq(document).ready(function () {

        //Block Multiple encounter entry on same date
        blockEncounterOnSameDateEncounter(jq("#encounterDate").find("input[type=hidden]"),"block");

        //Validate MUAC
        var age  = ; 
        getMUACCodeFromMUACScoreByAge(age, "muac-score", "muac-code");

        //Disable encounter date field
        disableEncounterDate();

        disable_fields("165424");        //partner pre art number
        disable_fields("165479");        //patner art number
        disable_fields("165422")         //tfv for partner
        disable_fields("90203");         //who clinical stage
        disable_fields("5497");          //cd4 count
        disable_fields("159376");        //cd4 date
        disable_fields("163023");        //viral load date
        disable_fields("1305");          //viral load results
        disable_fields("856");           //viral load copies
        disable_fields("99081");         //place where partner was linked for art
        disable_fields("99431");         //Art number
        disable_fields("165478");        //other tb diagnosed

        jq("#woman-emtct-code").change(function () {

            var emtctCode = jq(this).find("select").find(":selected").val();

            if(emtctCode == 99315 || emtctCode == 99736 || emtctCode == 99738 || emtctCode == 99741) { // if woman emtc code corresponds to an HIV positive status

                enable_fields("90203");  //who clinical stage
                enable_fields("163023"); //viral load date  99315
                enable_fields("5497");   //cd4 count
                enable_fields("159376"); //cd4 date

            } else {

                disable_fields("90203");  //who clinical stage
                disable_fields("163023"); //viral load date
                disable_fields("5497");   //cd4 count
                disable_fields("159376"); //cd4 date
            }
        });

        jq("#partner-emtct-code").change(function () {

            var emtctCode = jq(this).find("select").find(":selected").val();

            if(emtctCode == 99315 || emtctCode == 99736 || emtctCode == 99738 || emtctCode == 99741) { // if partner emtc code corresponds to an HIV positive status
                jq("#165422").find("input[type$='radio']").attr("disabled", false);

            } else {
                jq("#165422").find("input[type$='radio']").attr("disabled", true);
            }
        });

        jq("#162982").change(function () {

            if(jq(this).find("input[type='radio']:checked").val() == 1065) { 
                enable_fields("99081");
                enable_fields("165424");
                enable_fields("165479");
                enable_fields("99431");
                enable_fields("165422");

            } else {
                disable_fields("99081");
                disable_fields("165424");
                disable_fields("165479");
                disable_fields("99431");
                disable_fields("165422");
            }
        });

        //enable/disable viral load results field when viral load date is provided
        jq("#163023").change(function () {
            if (jq(this).find("input").val() !== " ") {
                enable_fields("1305");  //viral load result
            } else {
                disable_fields("1305");
            }
        });

        //enable/disable viral load copies field when viral load result is provided
        jq("#1305").change(function () {
            if (jq(this).find("select").find(":selected").val() == 1301) {
                enable_fields("856"); //viral load copies
            } else {
                disable_fields("856");
            }
        });

        //enable viral load copies field
        jq("#1305").change(function () {
            var viral_load_status = getValue('1305.value');
            if (viral_load_status == "") {
                jq("#viral-load-status-div").hide();
            }
            if (viral_load_status == 1301) {
                /* Detected */

                    setValue('856.value', '');


                var viral_load_copies = getValue('856.value');
                if(viral_load_copies == 1) {
                    jq("#viral-load-status-div").hide();
                    setValue('856.value', '');
                }
            }
            if (viral_load_status == 1306) {
                /* Not Detected */
                jq("#viral-load-status-div").hide();
                setValue('1305.value', 1);
            }
        });

        //trigger a change when the form loads
        jq("#1305").change();

        //determine viral load status
        jq("#856").change(function () {
            jq("#viral-load-status-div").show();
            var vl_copies = jq(this).find("input").val();
            if (vl_copies ==" " || vl_copies == 0) {
                jq("#viral-load-status-div").hide();
            }
            if (vl_copies < 1000) {
                jq("#viral-load-status-div").html("Suppressed");
            } else if (vl_copies >= 1000) {
                jq("#viral-load-status-div").html("Non-suppressed");
            } else {
                jq("#viral-load-status-div").hide();
            }
        });

        jq('#tb-status').change(function () {

            if (getValue('tb-status.value') == 165300)
                enable_fields("165478");
            else
                disable_fields("165478");
        });

    });
}
